definition.script:
node {
   // Mark the code checkout 'stage'....
   stage "Checkout"
   // Get some code from a GitHub repository
   git url: "https://github.com/randax/hello-world.git"


   // Mark the code build 'stage'....
   stage "npm install"
   // Run the maven build
   sh "npm install"

   stage "Build docker image"
   sh "docker build -t minion2/hello-world:latest ."

   stage "Push docker image to registry"
   sh "docker push minion2/hello-world:latest "


}
